import { Typography } from "@mui/material";
import Button from "@mui/material/Button";
import React, { useState } from "react";

const StoryTeller = ({ story, name, onStoryEnd }) => {
    const [currentPage, setCurrentPage] = useState(0);
    useState(() => {
        setCurrentPage(0);
    }, [story]);

    const handleNext = () => {
        if (currentPage < story.length - 2) {
            setCurrentPage(currentPage + 1);
        } else {
            setCurrentPage(currentPage + 1);
            onStoryEnd();
        }
    };

    const parseStory = (story) => {
      const boldRegex = /\[(.*?)\]/g;
      const parsedStory = story.replace(/<name>/g, name).replace(boldRegex, "<strong>$1</strong>");
      return <Typography dangerouslySetInnerHTML={{ __html: parsedStory }} />;
    };

    return (
        <div className="StoryTeller">
            {parseStory(story[currentPage])}

            {currentPage < story.length - 1 ? <Button onClick={handleNext}>Next</Button> : null}
        </div>
    );
};

export default StoryTeller;
